import CoreUtil from '../core/core.util';

import arrToString from './utils/arrToString';
import toSentenceCase from './utils/toSentenceCase';
import toTitleCase from './utils/toTitleCase';

const StringUtil = {
  arrToString: CoreUtil.takes(arrToString, 'array'),
  toSentenceCase: CoreUtil.takes(toSentenceCase, 'string'),
  toTitleCase: CoreUtil.takes(toTitleCase, 'string')
};

export default StringUtil;

/**
 *
 * @category StringUtil
 * @param {Array} array array to convert to string
 * @param {String} delimiter characater to use in  combining to string
 * @returns {String} Returns a string.
 * @example
 *
 * StringUtil.arrToString(['The', 'quick', 'brown'], '-');
 * // => 'The-brown-fox'
 */
const arrToString = (array, delimiter) => array.join(delimiter);

export default arrToString;

/**
 * Converts a string to Sentence case
 *
 * @category StringUtil
 * @param {String} string string to convert to Sentence case.
 * @returns {String} Returns a Sentence case string.
 * @example
 *
 * StringUtil.toSentenceCase('THE QUICK BROWN FOX');
 * // => 'The quick brown fox'
 */
const toSentenceCase = (string) => {
  const returnStr = string.toLowerCase();
  return returnStr.charAt(0).toUpperCase() + returnStr.slice(1);
};

export default toSentenceCase;

// https://stackoverflow.com/a/196991/4620773

/**
 * Converts a string to a Title Case
 *
 * @category StringUtil
 * @param {String} string string to convert to Title Case
 * @returns {String} Returns a Tile Case string.
 * @example
 *
 * StringUtil.toTitleCase('the quick brown fox');
 * // => 'The Quick Brown Fox'
 */
const toTitleCase = string => string.toLowerCase().replace(
  /\w\S*/g,
  txt => txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
);

export default toTitleCase;
